import gulp from 'gulp';
import yargs from 'yargs';
import sass from 'gulp-sass';
import cleanCSS from 'gulp-clean-css';
import gulpif from 'gulp-if';
import sourcemaps from 'gulp-sourcemaps';
import imagemin from 'gulp-imagemin';
import del from 'del';
import webpack from 'webpack-stream';
import named from 'vinyl-named';
import browserSync from 'browser-sync';
import zip from 'gulp-zip';
import replace from 'gulp-replace';
import info from './package.json';
import rename from 'gulp-rename';
import wpPot from 'gulp-wp-pot';


const server = browserSync.create();
const PRODUCTION = yargs.argv.prod;

// function defaultTask(cb) {
//   // place code for your default task here
//   console.log('Heyy');
//   cb();
// }

const paths = {
  rename: {
    src: ['archive-_themename_portfolio.php', 'single-_themename_portfolio.php', 'taxonomy-_themename_skills.php', 'taxonomy-_themename_project_type.php']
  },
  styles: {
    src: ['src/assets/scss/bundle.scss', 'src/assets/scss/admin.scss', 'src/assets/scss/editor.scss'],
    dest: 'dist/assets/css'
  },
  images: {
    src: 'src/assets/images/**/*.{jpg,jpeg,png,svg,gif}',
    dest: 'dist/assets/images'
  },
  scripts: {
    src: ['src/assets/js/bundle.js', 'src/assets/js/admin.js', 'src/assets/js/customize-preview.js'],
    dest: 'dist/assets/js'
  },
  plugins: {
    src: ['../../plugins/_themename-metaboxes/packaged/*',
          '../../plugins/_themename-post-types/packaged/*'],
    dest: ['lib/plugins']
  },
  other: {
    src: ['src/assets/**/*', '!src/assets/{images,js,scss}', '!src/assets/{images,js,scss}/**/*'],
    dest: 'dist/assets'
  },
  package: {
    src: ['**/*', '!.vscode', '!node_modules{,/**}', '!packaged{,/**}', '!src{./**}', '!.babelrc', '!.gitignore', '!gulpfile.babel.js', '!package.json', '!package-lock.json',
    '!archive-_themename_portfolio.php','!single-_themename_portfolio.php','!taxonomy-_themename_skills.php', '!taxonomy-_themename_project_type.php'
  ],
    dest: 'packaged'
  }
}

export const pot = () => {
  return gulp
    .src('**/*.php')
    .pipe(
      wpPot({
        domain: "_themename",
        package: info.name
      })
    )
    .pipe(gulp.dest(`language/${info.name}.pot`));
}

export const delete_replaced_filename = () => {
  return del(
    paths.rename.src.map(filename => filename.replace("_themename", info.name))
  )
}

export const replace_filenames = () => {
  return gulp
    .src(paths.rename.src)
    .pipe(
      rename(path => {
        path.basename = path.basename.replace("_themename", info.name);
      })
    )
    .pipe(gulp.dest("./"));
}


export const serve = (cb) => {
  server.init({
    proxy: {
      target: "http://localhost/WordPress-premium-theme-Dev/"
    },
    logLevel: 'debug',
    logConnections: true 
  });
  cb();
}

export const reload = (cb) => {
    server.reload();
    cb();
}

export const clean = () => del(['dist']);

export const styles = () => {
    return gulp.src(paths.styles.src)
        .pipe(gulpif(!PRODUCTION, sourcemaps.init()))
        .pipe(sass().on('error', sass.logError))
        .pipe(gulpif(PRODUCTION, cleanCSS({compatibility: 'ie8'})))
        .pipe(gulpif(!PRODUCTION, sourcemaps.write()))
        .pipe(gulp.dest(paths.styles.dest))
        .pipe(server.stream());
}

export const images = () => {
    return gulp.src(paths.images.src)
        .pipe(gulpif(PRODUCTION, imagemin()))
        .pipe(gulp.dest(paths.images.dest));
}

export const watch = () => {
  gulp.watch('src/assets/scss/**/*.scss', styles);
  gulp.watch('src/assets/js/**/*.js', gulp.series(scripts, reload));
  gulp.watch('**/*.php', reload);
  gulp.watch(paths.images.src, gulp.series(images, reload));
  gulp.watch(paths.other.src, gulp.series(copy, reload));
}

export const copy = () => {
  return gulp.src(paths.other.src)
        .pipe(gulp.dest(paths.other.dest));
}

export const copyPlugins = () => {
  return gulp.src(paths.plugins.src)
        .pipe(gulp.dest(paths.plugins.dest));
}

export const scripts = () => {
    return gulp.src(paths.scripts.src)
      .pipe(named())
      .pipe(webpack({
        watch: true,
        module: {
          rules: [
            {
              test: /\.js$/,
              use: {
                loader: 'babel-loader',
                options: {
                    presets: ["@babel/preset-env"]  
                }
              }
            }
          ]
        },
        output: {
          filename: '[name].js'
        },
        externals: {
          jquery: 'jQuery'
        },
        devtool: !PRODUCTION ? 'inline-source-map' : false
      }))
      .pipe(gulp.dest(paths.scripts.dest));
}

export const compress = () => {
    return gulp.src(paths.package.src)
        .pipe(gulpif((file) => (file.relative.split('.').pop() !== 'zip'), replace('_themename', info.name)))
        .pipe(zip(`${info.name}.zip`))
        .pipe(gulp.dest(paths.package.dest));
}

export const dev = gulp.series( clean, gulp.parallel(styles, scripts, images, copy, serve, watch));

export const build = gulp.series(clean, gulp.parallel(styles, scripts,images, copy, copyPlugins, pot));
export const bundle = gulp.series(build, replace_filenames, compress, delete_replaced_filename);

export default dev;
